import React, {useState,useEffect} from "react";
import FormControl from '@material-ui/core/FormControl';
import NativeSelect from '@material-ui/core/NativeSelect';
import axios from "axios";
import Sdata from "./States"


const DistictPicker = (props) =>{
    const [item,setItem] = useState("");
    const [DiscData,setDiscData]=useState([]);
    const name =props.data;


    useEffect(()=>{
        async function getInData(){
            const Ddata=await axios.get("https://api.covid19india.org/state_district_wise.json");
            setDiscData(Ddata.data[name].districtData);
         
        }
        getInData();
        
    
    },[name]);
     

    function listToSet(list) {
        var _set = {};
        if (Array.isArray(list)) {
            for (var i = 0; i < list.length; i++) {
                _set[list[i]] = true;
            }
        }
        return _set;
    }
    
    function filterByKeys(obj, keep) {
        var result = {},
            unfiltered = keep === undefined,
            keys = listToSet(keep);
        if (Object.isObject(obj)) {
    
                if (unfiltered ||  (!unfiltered && keys[key])) {
                    result[key] = obj[key];
                }
            }
        
        return result;
    }

const ItemSearch = (event) => {
    let data=event.target.value;
    setItem(data);

    
    
    var Numbers = filterByKeys(DiscData, [ `${data}` ]);
    
    console.log(Numbers);

}



    return (

        <>
        <div className="input-group flex-nowrap">
        <span className="input-group-text" id="addon-wrapping">@</span>
        <input type="text" className="form-control" placeholder="seach city"  onChange={ItemSearch} value={item}/>
        </div>
                            <div className="table-responsive">
                    
                       <h4 className=" text-center p-2 " > {name ? `${name} District covid cases` : "State Wise Data"}</h4> 
                      
      
                            <table className=" table table-striped ">
                                <thead className="table-dark">
                                <tr >
                                        <td>District Name</td>
                                        <td>active</td>
                                        <td>confirmed</td>
                                        <td>deceased</td>
                                        <td>recovered</td>
                                    </tr>
                                </thead>


                                <tbody className="table-bordered">
                                    {
                                         
                                        DiscData ? Object.keys(DiscData).map(function (element,i) {
                                            let value = DiscData[element];
                                            
                                            return <tr key={i}>
                                            <td title={JSON.stringify(element)}>{element}</td>
                                            <td >{value["active"]}</td>
                                            <td >{value["confirmed"]}</td>
                                            <td>{value["deceased"]}</td>
                                            <td>{value["recovered"]}</td>
                                            </tr>;
                                        }) :null
                                    }
                                </tbody>
                                {/* <tbody>
                               
                                        {
                                            
                                            Object.keys(DiscData).forEach(key => {
                                                    let value = DiscData[key];
                                                    console.log(key);
                                                    
                                                   
                                                    return  <><tr key={key}>
                                                            <td>{key}</td>
                                                            <td>{value.active}</td>
                                                            <td>{value.confirmed}</td>
                                                            <td>{value.deceased}</td>
                                                            <td>{value.recovered}</td>
                                                        </tr></>
                                                   
                                                

                                                    })
                                        

                                        }
                                       
                                    
                                </tbody> */}
                            </table>
                     
                    
             

                    </div>

       
    
         
        </> 
    )
}

export default DistictPicker;
