import React, { useState, useEffect } from "react";
import axios from "axios";
import {Line,Bar} from "react-chartjs-2";


const Charts = () =>{

const [dailyData,setDailyData]=useState([]);

useEffect(()=>{
    async function GetData(){
        const  res=await axios.get(`https://covid19.mathdro.id/api/daily`);
        
        setDailyData(res);
       
    } 

    GetData();
    
});

console.log(dailyData.data);

const LineChart=(

    dailyData.length != 0 ? (<Line
                            data={{
                                labels:dailyData.data(({date})=>date),
                                datasets:[{
                                    data:dailyData.data(({confirmed})=>confirmed),
                                    label:"Infected",
                                    borderColor:"#3333ff",
                                    fill:true,
                                } , {
                                    data:dailyData.data(({deaths})=>deaths),
                                    label:"Deaths",
                                    borderColor:"red",
                                    fill:true,
                                } ],
                            }}
                            />): null
                );



    return (

        <>
            <div>
                {LineChart}
            </div>
        </>
    )
}

export default Charts;
