import React, { useState, useEffect } from "react";
import {FetchIndiadata} from "./api";
import IndiaCards from "./IndiaCards";
import StatePicker from "./StatePicker";

import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "../node_modules/bootstrap/dist/js/bootstrap.bundle";
import axios from "axios";


const Home=()=>{

const [InData,setInData]=useState();
useEffect(()=>{


    async function getInData(){
        const Idata=await FetchIndiadata();
        setInData(Idata);
    }

    getInData();



    async function getStateData(state){
        const Satedata=await axios.get("https://api.covidindiatracker.com/state_data.json");
        console.log(Satedata.data);
        
    }

    getStateData();



},[]);


const handleStatechange = (state) =>{
console.log(state);




}

    return (
        <>
            <div className="container-fluid nav_bg">
                <div className="row">
                    <div className="col-10 mx-auto">
                    <StatePicker handleStatechange={handleStatechange}/>
                    <IndiaCards data={InData}/>
                    </div>
                </div>
            </div>
        </>
    )




    
}

export default Home;
