import React, { useState, useEffect } from "react";
import {FetchIndiadata} from "./api";
import IndiaCards from "./IndiaCards";
import StatePicker from "./StatePicker";

import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "../node_modules/bootstrap/dist/js/bootstrap.bundle";
import axios from "axios";
import Sdata from "./States";


const Home=()=>{

const [InData,setInData]=useState();
const [SData,setSData]=useState();
const [stateName,setstateName]=useState();
useEffect(()=>{


    async function getInData(){
        const Idata=await FetchIndiadata();
        setInData(Idata);
    }

    getInData();



   

    async function getStateData(){
        const Statedata=await axios.get("https://api.covidindiatracker.com/state_data.json");
       
        
        // const SData = Statedata.data.filter["Sikkim"];
        const stateMap = Statedata.data.map((s)=>{return s.state});
        // console.log(stateMap);
        
        setstateName(stateMap);
        
        setSData(Statedata.data);
        
    }
    
    getStateData();

},[SData]);




const handleStatechange = (state) =>{

    let STATEData = SData.filter((s) =>  s.state == `${state}`);
    setSData(STATEData);

   }
console.log(SData);
    return (
        <>
            <div className="container-fluid nav_bg">
                <div className="row">
                    <div className="col-10 mx-auto">
                   {SData ? <StatePicker handleStatechange={handleStatechange} data={stateName}/> : null} 
                 
                   {
                        
                    SData ? ( SData.length==1 ? <IndiaCards data={SData}/> : <IndiaCards data={InData}/> ) : null


                    }
                    </div>
                </div>
            </div>
        </>
    )




    
}

export default Home;
