import React, {useState,useEffect} from "react";
import FormControl from '@material-ui/core/FormControl';
import NativeSelect from '@material-ui/core/NativeSelect';
import axios from "axios";
import Sdata from "./States"


const DistictPicker = (props) =>{


    // // const StateData = Sdata.map((state)=> state.name);
    // const StateData = (props.data[0].name);
    // console.log(StateData);

    const [DiscData,setDiscData]=useState([]);
    const name =props.data;
    console.log(name);


    useEffect(()=>{
        async function getInData(){
            const Ddata=await axios.get("https://api.covid19india.org/state_district_wise.json");
            
            setDiscData(Ddata.data[name].districtData);
        }
        getInData();
        
    
    },[name]);

    // async function getInData(){
    //     const Ddata=await axios.get("https://api.covid19india.org/state_district_wise.json");
    //     console.log(Ddata.data[name].districtData);
    // }
    console.log(DiscData);

    const InputEvent=(e)=>{
        
        props.handleStatechange(e.target.value);
        // getInData();
    }


    return (

        <>
<h1>District Wise Data</h1>

      {

        Object.values(DiscData).map((data,index)=>{
            const{active,confirmed,deceased,recovered}=data;

            return (
                <>
                    <table>
                        <thead>
                        <tr key={index}>
                                <td>active</td>
                                <td>confirmed</td>
                                <td>deceased</td>
                                <td>recovered</td>
                            </tr>
                        </thead>
                        <tbody>
                            <tr key={index}>
                                <td>{active}</td>
                                <td>{confirmed}</td>
                                <td>{deceased}</td>
                                <td>{recovered}</td>
                            </tr>
                        </tbody>
                    </table>
                </>
            )
        })
      } 
         
        </>
    )
}

export default DistictPicker;
