import React, {useState,useEffect} from "react";
import FormControl from '@material-ui/core/FormControl';
import NativeSelect from '@material-ui/core/NativeSelect';
import axios from "axios";
import Sdata from "./States"


const DistictPicker = (props) =>{

    const [DiscData,setDiscData]=useState([]);
    const name =props.data;
    console.log(name);


    useEffect(()=>{
        async function getInData(){
            const Ddata=await axios.get("https://api.covid19india.org/state_district_wise.json");
            
            setDiscData(Ddata.data[name].districtData);
        }
        getInData();
        
    
    },[name]);

    // async function getInData(){
    //     const Ddata=await axios.get("https://api.covid19india.org/state_district_wise.json");
    //     console.log(Ddata.data[name].districtData);
    // }
    console.log(DiscData);

    const InputEvent=(e)=>{
        
        props.handleStatechange(e.target.value);
        // getInData();
    }


    return (

        <>
<h1>District Wise Data</h1>

      
                            <table className=" table-bordered table ">
                                <thead>
                                <tr >
                                        <td>District Name</td>
                                        <td>active</td>
                                        <td>confirmed</td>
                                        <td>deceased</td>
                                        <td>recovered</td>
                                    </tr>
                                </thead>
                                <tbody>
                               
                                        {
                                            
                                            Object.keys(DiscData).forEach(key => {
                                                    let value = DiscData[key];
                                                    console.log(key);
                                                    
                                                   
                                                        <tr>
                                                            <td>{key}</td>
                                                            <td>{value.active}</td>
                                                            <td>{value.confirmed}</td>
                                                            <td>{value.deceased}</td>
                                                            <td>{value.recovered}</td>
                                                        </tr>
                                                   
                                                

                                                    })
                                        

                                        }
                                       
                                    
                                </tbody>
                            </table>
                     
                    
             



       
    
         
        </> 
    )
}

export default DistictPicker;
