import React, {useState,useEffect} from "react";
import FormControl from '@material-ui/core/FormControl';
import NativeSelect from '@material-ui/core/NativeSelect';
import axios from "axios";
import Sdata from "./States"


const DistictPicker = (props) =>{

    const [DiscData,setDiscData]=useState([]);
    const [StateData,setStateData]=useState();
    const name =props.data;
    console.log(name);


    useEffect(()=>{
        async function getInData(){
            const Ddata=await axios.get("https://api.covid19india.org/state_district_wise.json");
            
            setDiscData(Ddata.data[name].districtData);
           
        }
        getInData();
        
    
    },[name]);
    useEffect(()=>{

        async function getSdata(){
            const Statedata=await axios.get("https://api.rootnet.in/covid19-in/stats/latest");
            
            setStateData(Statedata);
        }
        getSdata();

    },[]);
    
    console.log(StateData);

    const InputEvent=(e)=>{
        
        props.handleStatechange(e.target.value);
        // getInData();
    }


    return (

        <>
                       <h4 className=" text-center p-2 " > {name ? `${name} District covid cases` : "State Wise Data"}</h4> 

      
                            <table className=" table table-striped ">
                                <thead className="table-dark">
                                <tr >
                                        <td>District Name</td>
                                        <td>active</td>
                                        <td>confirmed</td>
                                        <td>deceased</td>
                                        <td>recovered</td>
                                    </tr>
                                </thead>


                                <tbody className="table-bordered">
                                    {
                                         
                                        DiscData ? Object.keys(DiscData).map(function (element) {
                                            let value = DiscData[element];
                                            return <tr>
                                            <td>{element}</td>
                                            <td>{value["active"]}</td>
                                            <td>{value["confirmed"]}</td>
                                            <td>{value["deceased"]}</td>
                                            <td>{value["recovered"]}</td>
                                            </tr>;
                                        }) :null
                                    }
                                </tbody>
                                {/* <tbody>
                               
                                        {
                                            
                                            Object.keys(DiscData).forEach(key => {
                                                    let value = DiscData[key];
                                                    console.log(key);
                                                    
                                                   
                                                    return  <><tr key={key}>
                                                            <td>{key}</td>
                                                            <td>{value.active}</td>
                                                            <td>{value.confirmed}</td>
                                                            <td>{value.deceased}</td>
                                                            <td>{value.recovered}</td>
                                                        </tr></>
                                                   
                                                

                                                    })
                                        

                                        }
                                       
                                    
                                </tbody> */}
                            </table>
                     
                    
             



       
    
         
        </> 
    )
}

export default DistictPicker;
